mapper.select:
select sas.id, sas.annt_id, sas.user_id, sas.read_flag, sa.file_list, sa.send_count, sa.read_count, sa.sender_depart, sa.titile as titile, sa.msg_content as msg_content, sa.sender as sender, sa.priority as priority, sa.msg_category, sa.send_time as send_time, sa.bus_id as bus_id, sa.open_type as open_type, sa.open_page as open_page, sa.msg_abstract from sys_announcement_send sas left join sys_announcement sa ON sas.annt_id = sa.id where sa.send_status = '1' and sa.del_flag = '0' and sas.user_id = #{announcementSendModel.userId}
and sa.msg_category = '1' or sa.msg_category = '2'
and sa.msg_category = '3'
and sa.msg_category = '1'
and sa.titile LIKE concat(concat('%',#{announcementSendModel.titile}),'%')
and sa.sender LIKE concat(concat('%',#{announcementSendModel.sender}),'%')
and sas.read_flag = #{announcementSendModel.readFlag}
and sa.bus_type = #{announcementSendModel.busType}
and (sa.bus_type != 'bpm' or sa.bus_type is null)
and sa.msg_category = #{announcementSendModel.msgCategory}
order by sas.read_flag,sa.send_time desc

SELECT sasu.id, sasu.annt_id, sasu.user_id, sasu.username, sasu.org_code, sasu.read_flag, sasu.read_time, sasu.create_time, sasu.is_delay, sasu.submit_remark, sasu.submit_file, sa.file_list, sa.sender_depart, sa.titile AS titile, sa.sender AS sender, sa.priority AS priority, sa.msg_category, sa.send_time AS send_time, sa.bus_id AS bus_id FROM ( SELECT sas.*, u.username, u.org_code FROM sys_announcement_send sas INNER JOIN sys_user u ON sas.user_id = u.id ) sasu LEFT JOIN sys_announcement sa ON sasu.annt_id = sa.id WHERE sa.del_flag = '0' and sasu.annt_id = #{announcementSendModel.anntId}
and sa.titile LIKE concat(concat('%',#{announcementSendModel.titile}),'%')
and sa.sender LIKE concat(concat('%',#{announcementSendModel.sender}),'%')
and sasu.read_flag = #{announcementSendModel.readFlag}
and sa.bus_type = #{announcementSendModel.busType}
and (sa.bus_type != 'bpm' or sa.bus_type is null)
and sa.msg_category = #{announcementSendModel.msgCategory}
order by sasu.read_flag,sasu.read_time desc
